import React, { Component } from 'react';
import './App.css';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import data from './data.json';
console.log(data);
const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});

var actualIndex = '';

function compare(a, b) {
  if(a[actualIndex] > b[actualIndex]) return 1;
  if(a[actualIndex] < b[actualIndex]) return -1;
  return 0;
}

function sortByIndex(index) {
  actualIndex = index;
  data.sort(function(a,b) {
    if(a[actualIndex] > b[actualIndex]) return 1;
  if(a[actualIndex] < b[actualIndex]) return -1;
  return 0;
  });
  console.log(data);
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      persons: data
    }
  }

  render() {
    return (
      <div className="App">
        <h1>nata.house frontend internship</h1>
        <Paper >
          <Table>
            <TableHead>
              <TableRow>
                <TableCell onClick={function() {
                  sortByIndex('name');
                  console.log(this.state.persons)
                }}>Name</TableCell>
                <TableCell onClick={function() {
                  sortByIndex('address');
                  this.state.persons = data;
                }}>Address</TableCell>
                <TableCell onClick={function() {
                  sortByIndex('city');
                  this.state.persons = data;
                }}>City</TableCell>
                <TableCell onClick={function() {
                  sortByIndex('region');
                  this.state.persons = data;
                }}>Region</TableCell>
                <TableCell onClick={function() {
                  sortByIndex('country');
                  this.state.persons = data;
                }}>Country</TableCell>
                <TableCell onClick={function() {
                  sortByIndex('birthday');
                  this.state.persons = data;
                }}>Birthday</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                this.state.persons.map(person => {
                  return (
                    <TableRow>
                      <TableCell>{person.name}</TableCell>
                      <TableCell>{person.address}</TableCell>
                      <TableCell>{person.city}</TableCell>
                      <TableCell>{person.region}</TableCell>
                      <TableCell>{person.country}</TableCell>
                      <TableCell>{person.birthday}</TableCell>
                    </TableRow>
                  );
                })
              }
            </TableBody>
          </Table>
        </Paper>

      </div>
    );
  }
}
export default withStyles(styles)(App);
