import React, { Component } from 'react';
import './App.css';
import { withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import data from './data.json';
var originalData = data.slice();
const styles = theme => ({
  root: {
    width: '100%',
    marginTop: theme.spacing.unit * 3,
    overflowX: 'auto',
  },
  table: {
    minWidth: 700,
  },
});

function sortByIndex(index, order) {
  data.sort(function(a,b) {
    if(a[index] > b[index]) return order;
    if(a[index] < b[index]) return -1 * order
  return 0;
  });
}

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      persons: data,
      order: {
        name: 0,
        address: 0,
        city: 0,
        region: 0,
        country: 0,
        birthday: 0,
      }
    }
  }

  render() {
    return (
      <div className="App">
        <h1>nata.house frontend internship</h1>
        <Paper >
          <Table>
            <TableHead>
              <TableRow>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('name', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('name', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>Name</TableCell>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('address', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('address', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>Address</TableCell>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('city', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('city', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>City</TableCell>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('region', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('region', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>Region</TableCell>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('country', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('country', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>Country</TableCell>
                <TableCell onClick={() => {
                  if(this.state.order === 0) {
                    sortByIndex('birthday', 1);
                    this.setState({persons: data, order: 1});
                  }
                  else if(this.state.order === 1) {
                    sortByIndex('birthday', -1);
                    this.setState({persons: data, order: -1});
                  }
                  else if(this.state.order === -1) {
                    this.setState({persons: originalData, order: 0});
                  }
                }}>Birthday</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {
                this.state.persons.map(person => {
                  return (
                    <TableRow>
                      <TableCell>{person.name}</TableCell>
                      <TableCell>{person.address}</TableCell>
                      <TableCell>{person.city}</TableCell>
                      <TableCell>{person.region}</TableCell>
                      <TableCell>{person.country}</TableCell>
                      <TableCell>{person.birthday}</TableCell>
                    </TableRow>
                  );
                })
              }
            </TableBody>
          </Table>
        </Paper>

      </div>
    );
  }
}
export default withStyles(styles)(App);
